    /**
     * The yaw component of the angle 
     */
    yaw: number;
    
    /**
     * The roll component of the angle 
     */
    roll: number;
    
    /**
     * The pitch component of the angle 
     */
    p: number;
    
    /**
     * The yaw component of the angle 
     */
