PrintTable( tbl )
local tbl2 = table.ClearKeys( tbl, true )
print( "<nowiki>===== After =====</nowiki>" )
PrintTable( tbl2 )

local output = table.CollapseKeyValue( {
	{ Key = "mykey1", Value = "myvalue1" },
	{ Key = 123, Value = 1345 },
	{ Key = 1345, Value = "myvalue1" },
} )

